// src/Chatbot.js
import React, { useState } from "react";
import axios from "axios";

function Chatbot() {
  const [input, setInput] = useState("");
  const [chat, setChat] = useState([]);

  const handleSend = async () => {
    if (!input.trim()) return;

    setChat([...chat, { sender: "user", message: input }]);

    try {
      const res = await axios.post("http://localhost:5000/api/chat", {
        message: input,
      });

      setChat((prev) => [...prev, { sender: "bot", message: res.data.reply }]);
      setInput("");
    } catch (err) {
      setChat((prev) => [...prev, { sender: "bot", message: "‚ö†Ô∏è Error talking to the bot." }]);
    }
  };

  return (
    <div style={{ padding: "1rem", maxWidth: 500, margin: "auto", background: "#fefefe", borderRadius: "12px", boxShadow: "0 0 10px rgba(0,0,0,0.1)" }}>
      <h3>ü§ñ Chat with CareBot</h3>
      <div style={{ maxHeight: 300, overflowY: "auto", marginBottom: "1rem" }}>
        {chat.map((msg, idx) => (
          <div key={idx} style={{ textAlign: msg.sender === "user" ? "right" : "left", margin: "0.5rem 0" }}>
            <span style={{
              background: msg.sender === "user" ? "#dcf8c6" : "#eee",
              padding: "8px 12px",
              borderRadius: "20px",
              display: "inline-block",
              maxWidth: "80%"
            }}>
              {msg.message}
            </span>
          </div>
        ))}
      </div>
      <div style={{ display: "flex", gap: "8px" }}>
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type a message..."
          style={{ flex: 1, padding: "10px", borderRadius: "8px", border: "1px solid #ccc" }}
        />
        <button onClick={handleSend} style={{ padding: "10px 16px", borderRadius: "8px", background: "#007bff", color: "#fff", border: "none" }}>
          Send
        </button>
      </div>
    </div>
  );
}

export default Chatbot;
