import { useEffect, useState } from "react";
import axios from "axios";

const Gallery = () => {
  const [gallery, setGallery] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/gallery");
        console.log("API Response:", response.data);
        if (Array.isArray(response.data)) {
          setGallery(response.data);
        } else if (response.data && Array.isArray(response.data.images)) {
          setGallery(response.data.images);
        } else {
          throw new Error("Unexpected response format");
        }
      } catch (error) {
        console.error("Error fetching images:", error);
        setError("Failed to load images");
      } finally {
        setLoading(false);
      }
    };
    fetchImages();
  }, []);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    setSelectedFile(file);
    setPreview(URL.createObjectURL(file));
  };

  const handleUpload = async () => {
    if (!selectedFile) return;
    const formData = new FormData();
    formData.append("file", selectedFile);
    try {
      await axios.post("http://localhost:5000/api/upload", formData);
      alert("Image uploaded successfully!");
      setSelectedFile(null);
      setPreview(null);
      setLoading(true);
      const response = await axios.get("http://localhost:5000/api/gallery");
      setGallery(response.data);
    } catch (error) {
      console.error("Error uploading image:", error);
      alert("Failed to upload image");
    }
  };

  return (
    <div style={{ 
      padding: "40px 20px",
      background: "linear-gradient(135deg, #8a63f2 0%, #ff6b6b 100%)",
      minHeight: "100vh",
      fontFamily: "'Poppins', sans-serif"
    }}>
      <div style={{ 
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
        marginBottom: "40px",
        padding: "20px",
        background: "rgba(255, 255, 255, 0.1)",
        backdropFilter: "blur(10px)",
        borderRadius: "15px",
        boxShadow: "0 8px 32px rgba(0, 0, 0, 0.1)",
        border: "1px solid rgba(255, 255, 255, 0.2)"
      }}>
        <h3 style={{ 
          margin: 0,
          color: "white",
          fontSize: "2rem",
          fontWeight: "600",
          textShadow: "0 2px 4px rgba(0, 0, 0, 0.2)"
        }}>
          Art Gallery
        </h3>
        <div style={{ display: "flex", gap: "15px", alignItems: "center" }}>
          <label style={{
            position: "relative",
            overflow: "hidden",
            cursor: "pointer",
            background: "rgba(255, 255, 255, 0.1)",
            padding: "8px 15px",
            borderRadius: "8px",
            color: "white",
            transition: "all 0.3s ease",
            ":hover": {
              background: "rgba(255, 255, 255, 0.2)"
            }
          }}>
            Choose File
            <input 
              type="file" 
              onChange={handleFileChange} 
              style={{ 
                position: "absolute",
                left: 0,
                top: 0,
                opacity: 0,
                cursor: "pointer",
                width: "100%",
                height: "100%"
              }}
            />
          </label>
          {preview && <img 
            src={preview} 
            alt="Preview" 
            style={{ 
              width: "60px",
              height: "60px",
              borderRadius: "50%",
              border: "3px solid white",
              boxShadow: "0 4px 15px rgba(0, 0, 0, 0.2)"
            }} 
          />}
          <button 
            onClick={handleUpload} 
            disabled={!selectedFile}
            style={{
              padding: "10px 25px",
              background: selectedFile ? "#6b3ea5" : "#cccccc",
              color: "white",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
              fontSize: "1rem",
              fontWeight: "500",
              transition: "all 0.3s ease",
              ":hover": {
                transform: selectedFile ? "translateY(-2px)" : "none",
                boxShadow: selectedFile ? "0 5px 15px rgba(107, 62, 165, 0.4)" : "none"
              }
            }}
          >
            Upload
          </button>
        </div>
      </div>

      {loading ? (
        <div style={{
          textAlign: "center",
          padding: "40px",
          color: "rgba(255, 255, 255, 0.8)"
        }}>
          <p>üñºÔ∏è Loading artworks...</p>
        </div>
      ) : error ? (
        <div style={{
          textAlign: "center",
          padding: "20px",
          background: "rgba(255, 255, 255, 0.1)",
          borderRadius: "10px",
          maxWidth: "500px",
          margin: "0 auto",
          color: "#ffeb3b"
        }}>
          <p>‚ö†Ô∏è {error}</p>
        </div>
      ) : gallery.length > 0 ? (
        <div style={{ 
          display: "grid",
          gridTemplateColumns: "repeat(auto-fit, minmax(250px, 1fr))",
          gap: "25px",
          padding: "20px 0"
        }}>
          {gallery.map((image, index) => (
            <div 
              key={index} 
              style={{ 
                position: "relative",
                borderRadius: "15px",
                overflow: "hidden",
                boxShadow: "0 8px 30px rgba(0, 0, 0, 0.2)",
                transition: "transform 0.3s ease",
                ":hover": {
                  transform: "translateY(-5px)"
                }
              }}
            >
              <img
                src={image.imageUrl || image.url}
                alt={`Artwork ${index + 1}`}
                style={{ 
                  width: "100%",
                  height: "300px",
                  objectFit: "cover",
                  display: "block"
                }}
              />
              <div style={{
                position: "absolute",
                bottom: 0,
                left: 0,
                right: 0,
                background: "linear-gradient(to top, rgba(0,0,0,0.7), transparent)",
                padding: "15px",
                color: "white",
                fontSize: "0.9rem"
              }}>
                Artwork #{index + 1}
              </div>
            </div>
          ))}
        </div>
      ) : (
        <div style={{
          textAlign: "center",
          padding: "40px",
          color: "rgba(255, 255, 255, 0.8)"
        }}>
          <p>üñºÔ∏è No masterpieces found yet!</p>
        </div>
      )}
    </div>
  );
};

export default Gallery;
