import { useEffect, useState } from "react";
import axios from "axios";

const Gallery = () => {
  const [gallery, setGallery] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);
  const [isUploading, setIsUploading] = useState(false);

  useEffect(() => {
    fetchImages();
  }, []);

  const fetchImages = async () => {
    try {
      setLoading(true);
      const response = await axios.get("http://localhost:5000/api/gallery");
      console.log("API Response:", response.data);
      if (Array.isArray(response.data)) {
        setGallery(response.data);
      } else if (response.data && Array.isArray(response.data.images)) {
        setGallery(response.data.images);
      } else {
        throw new Error("Unexpected response format");
      }
    } catch (error) {
      console.error("Error fetching images:", error);
      setError("Failed to load images");
    } finally {
      setLoading(false);
    }
  };

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) return;
    setIsUploading(true);
    const formData = new FormData();
    formData.append("file", selectedFile);
    try {
      await axios.post("http://localhost:5000/api/upload", formData);
      alert("Image uploaded successfully!");
      setSelectedFile(null);
      setPreview(null);
      fetchImages();
    } catch (error) {
      console.error("Error uploading image:", error);
      alert("Failed to upload image");
    } finally {
      setIsUploading(false);
    }
  };

  return (
    <div style={{
      padding: "20px",
      background: "linear-gradient(135deg, #8a63f2 0%, #ff6b6b 100%)",
      minHeight: "100vh",
      fontFamily: "'Poppins', sans-serif",
      color: "white",
      boxSizing: "border-box",
      width: "100%",
      margin: 0,
      overflow: "auto"
    }}>
      <div style={{
        maxWidth: "1200px",
        margin: "0 auto"
      }}>
        <div style={{
          display: "flex",
          flexDirection: "column",
          gap: "20px",
          marginBottom: "30px",
          padding: "20px",
          background: "rgba(0, 0, 0, 0.2)",
          borderRadius: "12px",
          boxShadow: "0 8px 32px rgba(0, 0, 0, 0.2)",
        }}>
          <h1 style={{
            margin: 0,
            color: "white",
            fontSize: "2rem",
            fontWeight: "700",
          }}>
            Digital Art Gallery
          </h1>
          
          <div style={{
            display: "flex",
            flexWrap: "wrap",
            gap: "15px",
            alignItems: "center"
          }}>
            <label style={{
              cursor: "pointer",
              background: "rgba(255, 255, 255, 0.2)",
              padding: "10px 20px",
              borderRadius: "8px",
              color: "white",
              fontWeight: "500",
              display: "inline-block"
            }}>
              Choose File
              <input
                type="file"
                onChange={handleFileChange}
                accept="image/*"
                style={{
                  position: "absolute",
                  left: "-9999px",
                }}
              />
            </label>
            
            <button
              onClick={handleUpload}
              disabled={!selectedFile || isUploading}
              style={{
                padding: "10px 25px",
                background: selectedFile ? "rgba(0, 0, 0, 0.4)" : "rgba(0, 0, 0, 0.2)",
                color: "white",
                border: "none",
                borderRadius: "8px",
                cursor: selectedFile ? "pointer" : "not-allowed",
                fontSize: "1rem",
                fontWeight: "500",
                opacity: isUploading ? 0.7 : 1
              }}
            >
              {isUploading ? "Uploading..." : "Upload Artwork"}
            </button>
            
            {preview && (
              <div style={{
                position: "relative",
                width: "80px",
                height: "80px",
                borderRadius: "8px",
                overflow: "hidden",
                border: "2px solid white"
              }}>
                <img
                  src={preview}
                  alt="Preview"
                  style={{
                    width: "100%",
                    height: "100%",
                    objectFit: "cover"
                  }}
                />
                <button
                  onClick={() => {
                    setPreview(null);
                    setSelectedFile(null);
                  }}
                  style={{
                    position: "absolute",
                    top: "0",
                    right: "0",
                    background: "rgba(0,0,0,0.7)",
                    color: "white",
                    border: "none",
                    width: "20px",
                    height: "20px",
                    fontSize: "12px",
                    display: "flex",
                    alignItems: "center",
                    justifyContent: "center",
                    cursor: "pointer"
                  }}
                >
                  ✕
                </button>
              </div>
            )}
          </div>
        </div>

        {loading ? (
          <div style={{
            textAlign: "center",
            padding: "40px",
            background: "rgba(0, 0, 0, 0.2)",
            borderRadius: "12px",
            marginBottom: "20px"
          }}>
            <p style={{ fontSize: "1.2rem" }}>Loading your masterpieces...</p>
            <div style={{
              width: "40px",
              height: "40px",
              border: "4px solid white",
              borderRadius: "50%",
              borderTopColor: "transparent",
              margin: "20px auto",
              animation: "spin 1s linear infinite"
            }} />
            <style>{`
              @keyframes spin {
                0% { transform: rotate(0deg); }
                100% { transform: rotate(360deg); }
              }
            `}</style>
          </div>
        ) : error ? (
          <div style={{
            textAlign: "center",
            padding: "20px",
            background: "rgba(0, 0, 0, 0.3)",
            borderRadius: "10px",
            maxWidth: "500px",
            margin: "0 auto",
            color: "white"
          }}>
            <p>⚠️ {error}</p>
          </div>
        ) : gallery.length > 0 ? (
          <div style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(250px, 1fr))",
            gap: "20px",
            width: "100%"
          }}>
            {gallery.map((image, index) => (
              <div
                key={index}
                style={{
                  position: "relative",
                  borderRadius: "12px",
                  overflow: "hidden",
                  boxShadow: "0 8px 20px rgba(0, 0, 0, 0.3)",
                  background: "rgba(0, 0, 0, 0.2)",
                }}
              >
                <div style={{
                  height: "250px",
                  overflow: "hidden",
                  position: "relative"
                }}>
                  <img
                    src={image.imageUrl || image.url}
                    alt={`Artwork ${index + 1}`}
                    style={{
                      width: "100%",
                      height: "100%",
                      objectFit: "cover",
                      transition: "transform 0.5s ease"
                    }}
                    onMouseOver={(e) => e.target.style.transform = "scale(1.05)"}
                    onMouseOut={(e) => e.target.style.transform = "scale(1)"}
                  />
                </div>
                <div style={{
                  padding: "15px",
                  borderTop: "1px solid rgba(255, 255, 255, 0.1)"
                }}>
                  <h3 style={{ margin: 0, color: "white" }}>
                    Artwork #{index + 1}
                  </h3>
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div style={{
            textAlign: "center",
            padding: "40px",
            background: "rgba(0, 0, 0, 0.2)",
            borderRadius: "12px",
            color: "white"
          }}>
            <p style={{ fontSize: "1.2rem" }}>🖼️ Your gallery is waiting for your first masterpiece!</p>
            <p>Upload an image to get started</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Gallery;
