import { useEffect, useState } from "react";
import axios from "axios";

const Gallery = () => {
  const [gallery, setGallery] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [selectedFile, setSelectedFile] = useState(null);
  const [preview, setPreview] = useState(null);

  useEffect(() => {
    const fetchImages = async () => {
      try {
        const response = await axios.get("http://localhost:5000/api/gallery");
        console.log("API Response:", response.data);
        if (Array.isArray(response.data)) {
          setGallery(response.data);
        } else if (response.data && Array.isArray(response.data.images)) {
          setGallery(response.data.images);
        } else {
          throw new Error("Unexpected response format");
        }
      } catch (error) {
        console.error("Error fetching images:", error);
        setError("Failed to load images");
      } finally {
        setLoading(false);
      }
    };
    fetchImages();
  }, []);

  const handleFileChange = (event) => {
    const file = event.target.files[0];
    if (file) {
      setSelectedFile(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleUpload = async () => {
    if (!selectedFile) return;
    const formData = new FormData();
    formData.append("file", selectedFile);
    try {
      await axios.post("http://localhost:5000/api/upload", formData);
      alert("Image uploaded successfully!");
      setSelectedFile(null);
      setPreview(null);
      setLoading(true);
      const response = await axios.get("http://localhost:5000/api/gallery");
      if (Array.isArray(response.data)) {
        setGallery(response.data);
      } else if (response.data && Array.isArray(response.data.images)) {
        setGallery(response.data.images);
      }
      setLoading(false);
    } catch (error) {
      console.error("Error uploading image:", error);
      alert("Failed to upload image");
    }
  };

  return (
    <div style={{
      padding: "0",
      margin: "0",
      background: "linear-gradient(135deg, #8a63f2 0%, #ff6b6b 100%)",
      minHeight: "100vh",
      fontFamily: "'Poppins', sans-serif",
      width: "100%",
      boxSizing: "border-box",
    }}>
      <div style={{
        maxWidth: "1000px",
        margin: "0 auto",
        padding: "20px",
      }}>
        {/* Centered header */}
        <div style={{
          textAlign: "center",
          marginBottom: "20px",
          padding: "20px",
          background: "rgba(255, 255, 255, 0.1)",
          borderRadius: "10px",
          maxWidth: "500px",
          margin: "0 auto 30px auto",
        }}>
          <h2 style={{
            margin: "0 0 15px 0",
            color: "white",
            fontSize: "24px",
            fontWeight: "bold",
          }}>
            Digital Art Gallery
          </h2>
          
          <div style={{
            display: "flex",
            justifyContent: "center",
            gap: "10px"
          }}>
            <label style={{
              cursor: "pointer",
              background: "rgba(255, 255, 255, 0.2)",
              padding: "8px 15px",
              borderRadius: "4px",
              color: "white",
              fontSize: "14px",
            }}>
              Choose File
              <input
                type="file"
                onChange={handleFileChange}
                accept="image/*"
                style={{
                  position: "absolute",
                  left: "-9999px",
                }}
              />
            </label>
            
            <button
              onClick={handleUpload}
              disabled={!selectedFile}
              style={{
                padding: "8px 15px",
                background: "rgba(0, 0, 0, 0.3)",
                color: "white",
                border: "none",
                borderRadius: "4px",
                cursor: selectedFile ? "pointer" : "not-allowed",
                fontSize: "14px",
                opacity: selectedFile ? 1 : 0.6
              }}
            >
              Upload Artwork
            </button>
          </div>
        </div>

        {loading ? (
          <div style={{
            textAlign: "center",
            padding: "20px",
            color: "white"
          }}>
            <p>üñºÔ∏è Loading artworks...</p>
          </div>
        ) : error ? (
          <div style={{
            textAlign: "center",
            padding: "10px",
            color: "white"
          }}>
            <p>‚ö†Ô∏è {error}</p>
          </div>
        ) : gallery.length > 0 ? (
          <div style={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
            gap: "15px"
          }}>
            {gallery.map((image, index) => (
              <div
                key={index}
                style={{
                  borderRadius: "8px",
                  overflow: "hidden",
                  boxShadow: "0 4px 8px rgba(0, 0, 0, 0.2)",
                }}
              >
                <img
                  src={image.imageUrl || image.url}
                  alt={`Artwork ${index + 1}`}
                  style={{
                    width: "100%",
                    height: "200px",
                    objectFit: "cover",
                    display: "block"
                  }}
                />
                <div style={{
                  padding: "8px",
                  background: "rgba(0, 0, 0, 0.5)",
                  color: "white",
                  fontSize: "12px",
                  textAlign: "center"
                }}>
                  Artwork #{index + 1}
                </div>
              </div>
            ))}
          </div>
        ) : (
          <div style={{
            textAlign: "center",
            padding: "20px",
            color: "white"
          }}>
            <p>üñºÔ∏è No masterpieces found yet!</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default Gallery;
