import React from 'react';
import { Link } from 'react-router-dom';

const Footer = () => {
  const currentYear = new Date().getFullYear();
  return (
    <footer style={{
      position: 'fixed',
      bottom: '0',
      left: '0',
      right: '0',
      zIndex: '1000',
      background: 'linear-gradient(135deg, #f3e5ff 0%, #e2c3ff 50%, #d8b4ff 100%)',
      color: '#5a3d7a',
      padding: '20px 0 10px',
      fontFamily: "'Poppins', sans-serif",
      boxShadow: '0 -5px 20px rgba(0,0,0,0.05)',
      borderTop: '1px solid rgba(255,255,255,0.3)'
    }}>
      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '0 20px',
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center'
      }}>
        <h3 style={{
          fontSize: 'clamp(14px, 2vw, 18px)',
          fontWeight: '600',
          marginBottom: '15px',
          textAlign: 'center'
        }}>
          <span style={{ color: '#5a3d7a', fontWeight: '600' }}>Â© {currentYear} Canvas of Care.</span>{' '}
          <span style={{ color: '#5a3d7a', fontWeight: '600' }}>All Rights Reserved</span>
        </h3>
        
        <div style={{
          display: 'flex',
          flexWrap: 'wrap',
          justifyContent: 'center',
          alignItems: 'center',
          gap: '12px',
          marginBottom: '10px'
        }}>
          <Link to="/privacy-policy" style={{
            color: '#5e35b1',
            textDecoration: 'none',
            padding: '5px 10px',
            borderRadius: '20px',
            transition: 'all 0.3s ease',
            ':hover': {
              background: 'rgba(255,255,255,0.3)',
            }
          }}>
            <span style={{ fontWeight: '500' }}>Privacy Policy</span>
          </Link>
          
          <div style={{ color: 'rgba(90, 61, 122, 0.3)', fontSize: '16px' }}>|</div>
          
          <Link to="/contact-us" style={{
            color: '#5e35b1',
            textDecoration: 'none',
            padding: '5px 10px',
            borderRadius: '20px',
            transition: 'all 0.3s ease',
            ':hover': {
              background: 'rgba(255,255,255,0.3)',
            }
          }}>
            <span style={{ fontWeight: '500' }}>Contact Us</span>
          </Link>
          
          <div style={{ color: 'rgba(90, 61, 122, 0.3)', fontSize: '16px' }}>|</div>
          
          <Link to="/about-us" style={{
            color: '#5e35b1',
            textDecoration: 'none',
            padding: '5px 10px',
            borderRadius: '20px',
            transition: 'all 0.3s ease',
            ':hover': {
              background: 'rgba(255,255,255,0.3)',
            }
          }}>
            <span style={{ fontWeight: '500' }}>About Us</span>
          </Link>
        </div>
        
        <p style={{ fontSize: '12px', display: 'flex', alignItems: 'center', color: '#7e57c2' }}>
          Crafted with <span style={{ color: '#d81b60', margin: '0 5px', fontSize: '14px' }}>â¥</span> for you
        </p>
      </div>
    </footer>
  );
};

export default Footer;
