function HospitalSelection() {
  const navigate = useNavigate();
  const [selectedId, setSelectedId] = useState(null);

  return (
    <div className="container">
      <h2 className="title">Hospitals</h2>
      <div className="grid">
        {hospitals.map((hospital) => (
          <div
            key={hospital.id}
            className={`card ${selectedId === hospital.id ? 'selected' : ''}`}
            onClick={() => {
              setSelectedId(hospital.id);
              setTimeout(() => navigate(`/doctors/${hospital.id}`), 1000);
            }}
          >
            <img src={hospital.image} alt={hospital.name} className="card-img" />
            <div className="card-content">
              <h3 className="card-title">{hospital.name}</h3>
              <p className="card-subtitle">Tap to view specialists</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
}
