import React, { useState, useRef, useEffect } from "react";
import CanvasDraw from "react-canvas-draw";
import "./NoteItCanvas.css";
import { useNavigate } from "react-router-dom";

const NoteItCanvas = () => {
  const [note, setNote] = useState("");
  const canvasRef = useRef(null);
  const [color, setColor] = useState("#000");
  const [brushSize, setBrushSize] = useState(5);
  const [isErasing, setIsErasing] = useState(false);
  const [tool, setTool] = useState("brush");
  const [shapes, setShapes] = useState([]);
  const [dragging, setDragging] = useState(false);
  const [draggedShapeIndex, setDraggedShapeIndex] = useState(null);
  const [offset, setOffset] = useState({ x: 0, y: 0 });
  const [toolActive, setToolActive] = useState(false);
  const [brushType, setBrushType] = useState("marker"); // Added brush types
  const [showColorPicker, setShowColorPicker] = useState(false);
  const [showBrushOptions, setShowBrushOptions] = useState(false);
  const [background, setBackground] = useState("#ffffff");
  const [currentShapeColor, setCurrentShapeColor] = useState("#ffffff");

  // Instagram-like color palette
  const colorPalette = [
    "#000000", "#1a1a1a", "#333333", "#4d4d4d", "#666666",
    "#ff0000", "#ff4000", "#ff8000", "#ffbf00", "#ffff00",
    "#00ff00", "#00ff40", "#00ff80", "#00ffbf", "#00ffff",
    "#0000ff", "#4000ff", "#8000ff", "#bf00ff", "#ff00ff"
  ];

  // Brush types with different effects
  const brushTypes = [
    { id: "marker", name: "Marker" },
    { id: "pencil", name: "Pencil" },
    { id: "neon", name: "Neon" },
    { id: "chalk", name: "Chalk" }
  ];

  const resizeCanvas = () => {
    if (canvasRef.current) {
      const container = canvasRef.current.canvasContainer;
      const canvas = container.querySelector("canvas");
      if (canvas) {
        canvas.width = container.offsetWidth;
        canvas.height = container.offsetHeight;
      }
    }
  };

  useEffect(() => {
    resizeCanvas();
    window.addEventListener("resize", resizeCanvas);
    return () => window.removeEventListener("resize", resizeCanvas);
  }, []);

  // Brush Icon Component
  const BrushIcon = () => (
    <svg
      className="w-6 h-6 text-gray-800 dark:text-white"
      aria-hidden="true"
      xmlns="http://www.w3.org/2000/svg"
      width="30"
      height="55"
      fill="currentColor"
      viewBox="0 0 24 24"
    >
      <path
        fillRule="evenodd"
        d="M14 4.182A4.136 4.136 0 0 1 16.9 3c1.087 0 2.13.425 2.899 1.182A4.01 4.01 0 0 1 21 7.037c0 1.068-.43 2.092-1.194 2.849L18.5 11.214l-5.8-5.71 1.287-1.31.012-.012Zm-2.717 2.763L6.186 12.13l2.175 2.141 5.063-5.218-2.141-2.108Zm-6.25 6.886-1.98 5.849a.992.992 0 0 0 .245 1.026 1.03 1.03 0 0 0 1.043.242L10.282 19l-5.25-5.168Zm6.954 4.01 5.096-5.186-2.218-2.183-5.063 5.218 2.185 2.15Z"
        clipRule="evenodd"
      />
    </svg>
  );

  const toggleEraser = () => {
    if (isErasing) {
      setColor("#000");
    } else {
      setColor("#FFFFFF");
    }
    setIsErasing(!isErasing);
  };

  const saveDrawing = () => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current.canvasContainer.children[1];
    const textCanvas = document.createElement("canvas");
    const textCtx = textCanvas.getContext("2d");

    const width = 800;
    const height = 600;
    textCanvas.width = width;
    textCanvas.height = height;

    textCtx.fillStyle = background;
    textCtx.fillRect(0, 0, width, height);
    textCtx.drawImage(canvas, 0, 0, canvas.width, canvas.height);

    shapes.forEach((shape) => {
      textCtx.fillStyle = shape.fillColor;
      textCtx.strokeStyle = color;
      textCtx.lineWidth = brushSize;

      if (shape.type === "rectangle" || shape.type === "square") {
        textCtx.fillRect(shape.x, shape.y, shape.width, shape.height);
        textCtx.strokeRect(shape.x, shape.y, shape.width, shape.height);
      } else if (shape.type === "diamond") {
        textCtx.beginPath();
        textCtx.moveTo(shape.x, shape.y - shape.height / 2);
        textCtx.lineTo(shape.x - shape.width / 2, shape.y);
        textCtx.lineTo(shape.x, shape.y + shape.height / 2);
        textCtx.lineTo(shape.x + shape.width / 2, shape.y);
        textCtx.closePath();
        textCtx.fill();
        textCtx.stroke();
      } else if (shape.type === "circle") {
        textCtx.beginPath();
        textCtx.arc(shape.x, shape.y, shape.radiusX, 0, 2 * Math.PI);
        textCtx.fill();
        textCtx.stroke();
      } else if (shape.type === "oval") {
        textCtx.beginPath();
        textCtx.ellipse(shape.x, shape.y, shape.radiusX, shape.radiusY, 0, 0, 2 * Math.PI);
        textCtx.fill();
        textCtx.stroke();
      } else if (shape.type === "triangle") {
        textCtx.beginPath();
        textCtx.moveTo(shape.x, shape.y);
        textCtx.lineTo(shape.x + shape.width / 2, shape.y - shape.height);
        textCtx.lineTo(shape.x + shape.width, shape.y);
        textCtx.closePath();
        textCtx.fill();
        textCtx.stroke();
      }
    });

    if (note) {
      textCtx.font = "20px Arial";
      textCtx.fillStyle = "#000";
      textCtx.fillText(note, 20, height - 30);
    }

    const dataUrl = textCanvas.toDataURL("image/png");
    const link = document.createElement("a");
    link.href = dataUrl;
    link.download = "my_drawing.png";
    link.click();
  };

  // New WhatsApp sharing function
  const shareViaWhatsApp = () => {
    if (!canvasRef.current) return;

    const canvas = canvasRef.current.canvasContainer.children[1];
    const tempCanvas = document.createElement("canvas");
    const tempCtx = tempCanvas.getContext("2d");

    tempCanvas.width = canvas.width;
    tempCanvas.height = canvas.height;

    tempCtx.fillStyle = background;
    tempCtx.fillRect(0, 0, tempCanvas.width, tempCanvas.height);
    tempCtx.drawImage(canvas, 0, 0);

    shapes.forEach((shape) => {
      tempCtx.fillStyle = shape.fillColor;
      tempCtx.strokeStyle = color;
      tempCtx.lineWidth = brushSize;

      if (shape.type === "rectangle" || shape.type === "square") {
        tempCtx.fillRect(shape.x, shape.y, shape.width, shape.height);
        tempCtx.strokeRect(shape.x, shape.y, shape.width, shape.height);
      } else if (shape.type === "diamond") {
        tempCtx.beginPath();
        tempCtx.moveTo(shape.x, shape.y - shape.height / 2);
        tempCtx.lineTo(shape.x - shape.width / 2, shape.y);
        tempCtx.lineTo(shape.x, shape.y + shape.height / 2);
        tempCtx.lineTo(shape.x + shape.width / 2, shape.y);
        tempCtx.closePath();
        tempCtx.fill();
        tempCtx.stroke();
      } else if (shape.type === "circle") {
        tempCtx.beginPath();
        tempCtx.arc(shape.x, shape.y, shape.radiusX, 0, 2 * Math.PI);
        tempCtx.fill();
        tempCtx.stroke();
      } else if (shape.type === "oval") {
        tempCtx.beginPath();
        tempCtx.ellipse(shape.x, shape.y, shape.radiusX, shape.radiusY, 0, 0, 2 * Math.PI);
        tempCtx.fill();
        tempCtx.stroke();
      } else if (shape.type === "triangle") {
        tempCtx.beginPath();
        tempCtx.moveTo(shape.x, shape.y);
        tempCtx.lineTo(shape.x + shape.width / 2, shape.y - shape.height);
        tempCtx.lineTo(shape.x + shape.width, shape.y);
        tempCtx.closePath();
        tempCtx.fill();
        tempCtx.stroke();
      }
    });

    if (note) {
      tempCtx.font = "20px Arial";
      tempCtx.fillStyle = "#000";
      tempCtx.fillText(note, 20, tempCanvas.height - 30);
    }

    const dataUrl = tempCanvas.toDataURL("image/png");
    const message = encodeURIComponent("Check out my drawing!");
    
    // For web implementation
    window.open(`https://api.whatsapp.com/send?text=${message}`, '_blank');
  };

  // Rest of your existing functions (drawShape, handleMouseDown, etc.) remain the same
  // ...

  const navigate = useNavigate();

  return (
    <div className="note-container">
      <div className="main-box">
        <div className="top-section">
          <div className="column">
            <h3>Tools</h3>
            <div className="button-row">
              <button
                onClick={() => handleToolChange("brush")}
                className={tool === "brush" ? "active-tool" : ""}
              >
                <BrushIcon />
              </button>
              <button onClick={toggleEraser}>
                {isErasing ? "Use Brush" : "Use Eraser"}
              </button>
              <button onClick={() => setShowBrushOptions(!showBrushOptions)}>
                Brush Type
              </button>
              {showBrushOptions && (
                <div className="brush-options-dropdown">
                  {brushTypes.map((brush) => (
                    <div
                      key={brush.id}
                      className="brush-option"
                      onClick={() => setBrushType(brush.id)}
                    >
                      {brush.name}
                    </div>
                  ))}
                </div>
              )}
            </div>
          </div>

          <div className="column">
            <h3>Colour</h3>
            <div className="color-picker-container">
              <button 
                onClick={() => setShowColorPicker(!showColorPicker)}
                className="color-preview"
                style={{ backgroundColor: color }}
              />
              {showColorPicker && (
                <div className="color-palette">
                  {colorPalette.map((c) => (
                    <div
                      key={c}
                      className="color-swatch"
                      style={{ backgroundColor: c }}
                      onClick={() => {
                        setColor(c);
                        setShowColorPicker(false);
                        setIsErasing(false);
                      }}
                    />
                  ))}
                </div>
              )}
            </div>
            <input
              type="range"
              min="1"
              max="20"
              value={brushSize}
              onChange={(e) => setBrushSize(Number(e.target.value))}
            />
            <span>Size: {brushSize}px</span>
          </div>

          <div className="column">
            <h3>Shapes</h3>
            <div className="shape-row">
              <button
                onClick={() => handleToolChange("rectangle")}
                className={tool === "rectangle" ? "active-tool" : ""}
              >
                Rectangle
              </button>
              <button
                onClick={() => handleToolChange("circle")}
                className={tool === "circle" ? "active-tool" : ""}
              >
                Circle
              </button>
            </div>
            <div className="shape-row">
              <button
                onClick={() => handleToolChange("triangle")}
                className={tool === "triangle" ? "active-tool" : ""}
              >
                Triangle
              </button>
              <button
                onClick={() => handleToolChange("diamond")}
                className={tool === "diamond" ? "active-tool" : ""}
              >
                Diamond
              </button>
            </div>
            <div className="shape-color-picker">
              <input
                type="color"
                value={currentShapeColor}
                onChange={(e) => setCurrentShapeColor(e.target.value)}
              />
              <span>Shape Color</span>
            </div>
          </div>

          <div className="column">
            <h3>Edit</h3>
            <button onClick={() => canvasRef.current?.clear()}>Clear</button>
            <button onClick={undoShape}>Undo</button>
            <div className="background-picker">
              <input
                type="color"
                value={background}
                onChange={(e) => setBackground(e.target.value)}
              />
              <span>Background</span>
            </div>
          </div>
        </div>

        <div
          onClick={drawShape}
          onMouseDown={handleMouseDown}
          onMouseMove={handleMouseMove}
          onMouseUp={handleMouseUp}
          style={{
            width: "100%",
            height: "500px",
            position: "relative",
            border: "1px solid #ccc",
            backgroundColor: background
          }}
        >
          <CanvasDraw
            ref={canvasRef}
            brushColor={color}
            brushRadius={tool === "brush" ? brushSize : 0}
            lazyRadius={0}
            canvasWidth={800}
            canvasHeight={500}
            hideGrid
            className="drawing-canvas"
          />
        </div>

        <textarea
          className="note-input"
          placeholder="Write your note..."
          value={note}
          onChange={(e) => setNote(e.target.value)}
        />

        <div className="btn-group">
          <button onClick={saveDrawing}>Save Drawing</button>
          <button onClick={shareViaWhatsApp} className="postcard-button">
            Share via WhatsApp
          </button>
          <button onClick={() => navigate('/gallery')}>Go to Gallery</button>
        </div>
      </div>
    </div>
  );
};

export default NoteItCanvas;
